function syncLanguageWithMenu(textName,linkName){
	$(".my-menu-top").find("li.nav-blink").each(function(index, element) {
            		$(this).find('a').each(function(index, element) { 
						if($.trim($(this).html())==textName){
							$(this).attr('href',linkName);
						}
					});
     });
}
$(document).ready(function(){
	if(window.location.pathname=='/products-services-singapore.html') { 
		if($.cookie('region')=='singapore') {} else {
			$.cookie('region', 'singapore', { expires:1, path: '/' });
		}
	}
	else if(window.location.pathname=='/products-services.html') { 
		if($.cookie('region')=='india') {} else {
			$.cookie('region', 'india', { expires:1, path: '/' });
		}
	}
	if($.cookie('region')!='undefined') {
		$("a[href='region.html']").removeClass('active');
		$("a[href='region.html']").removeClass('singapore');
		if($.cookie('region')=='singapore') {
			$("a[href='region.html']").addClass('active singapore');
			syncLanguageWithMenu("PRODUCTS &amp;&nbsp;SERVICES","products-services-singapore.html");
		}
		else if($.cookie('region')=='india') {
			//$("a[href='region.html']").addClass('');
			syncLanguageWithMenu("PRODUCTS &amp;&nbsp;SERVICES","products-services.html");
		}
		else {
			syncLanguageWithMenu("PRODUCTS &amp;&nbsp;SERVICES","products-services.html");
		}
	}
	else {
		syncLanguageWithMenu("PRODUCTS &amp;&nbsp;SERVICES","products-services.html");
	}
});